package com.cmw.springmvc;

public class Bean {
	private int x;
	private int y;
	
	public Bean() {
		// TODO Auto-generated constructor stub
	}
	
	public int getX() {
		return x;
	}
	public void setX(int x) {
		this.x = x;
	}
	public int getY() {
		return y;
	}
	public void setY(int y) {
		this.y = y;
	}
	public Bean(int x, int y) {
		super();
		this.x = x;
		this.y = y;
	}
	
	
	
}
